import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import soundIcon from './assets/sound-icon.png'; // Replace with your sound icon image path
import retroSong from './assets/retro-song.mp3'; // Replace with your retro song file path

const Home = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audio = new Audio(retroSong);

  const togglePlay = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="home-page">
      <div className="sound-logo-container" onClick={togglePlay}>
        <img src={soundIcon} alt="Sound Icon" className="sound-logo" />
        <p className="sound-status">{isPlaying ? "ðŸ”Š Playing" : "ðŸ”‡ Paused"}</p>
      </div>
      <h1 className="welcome-heading">Welcome to the 90's Vibes!</h1>
      <div className="button-container">
        <Link to="/downloads">
          <button>Downloads</button>
        </Link>
        <Link to="/characters">
          <button>Characters</button>
        </Link>
        <Link to="/games">
          <button>Fun & Games</button>
        </Link>
        <Link to="/crew">
          <button>Caste & Crew</button>
        </Link>
        <Link to="/gallery">
          <button>Gallery</button>
        </Link>
        <Link to="/review">
          <button>Reviews</button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
