import React, { useState } from 'react';
import './Games.css';

const Games = () => {
  const [randomNumber, setRandomNumber] = useState(Math.floor(Math.random() * 100) + 1);
  const [userGuess, setUserGuess] = useState('');
  const [message, setMessage] = useState('Try guessing a number between 1 and 100!');
  const [attempts, setAttempts] = useState(0);

  const handleGuess = () => {
    const guess = parseInt(userGuess, 10);
    setAttempts(attempts + 1);

    if (isNaN(guess)) {
      setMessage('Please enter a valid number!');
    } else if (guess < randomNumber) {
      setMessage('Too low! Try again.');
    } else if (guess > randomNumber) {
      setMessage('Too high! Try again.');
    } else {
      setMessage(`ðŸŽ‰ Correct! You guessed it in ${attempts} attempts. Hit reset to play again!`);
    }

    setUserGuess('');
  };

  const handleReset = () => {
    setRandomNumber(Math.floor(Math.random() * 100) + 1);
    setUserGuess('');
    setMessage('Try guessing a number between 1 and 100!');
    setAttempts(0);
  };

  return (
    <div className="games-container">
      <h1 className="retro-title">ðŸŽ® Friends - Guess the Number!</h1>
      <p className="game-instructions">It's the 90's. Can you guess the right number before your friends do?</p>

      <div className="game-area">
        <p className="game-message">{message}</p>
        <input
          type="number"
          value={userGuess}
          onChange={(e) => setUserGuess(e.target.value)}
          placeholder="Enter your guess..."
          className="guess-input"
        />
        <div className="button-container">
          <button onClick={handleGuess} className="game-button">Guess</button>
          <button onClick={handleReset} className="game-button reset">Reset</button>
        </div>
      </div>
    </div>
  );
};

export default Games;
