// src/Home.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import soundIcon from './assets/sound.png';
import { SoundContext } from './contexts/SoundContext';

const Home = () => {
  const { isPlaying, toggleSound } = useContext(SoundContext);

  return (
    <div className="home-page">
      <h1 className="welcome-heading">Welcome to the 90's Vibes!</h1>
      <div className="button-container">
        <Link to="/downloads">
          <button>Downloads</button>
        </Link>
        <Link to="/characters">
          <button>Characters</button>
        </Link>
        <Link to="/games">
          <button>Fun & Games</button>
        </Link>
        <Link to="/crew">
          <button>Caste & Crew</button>
        </Link>
        <Link to="/gallery">
          <button>Gallery</button>
        </Link>
        <Link to="/review">
          <button>Reviews</button>
        </Link>
      </div>
      <div className="sound-toggle" onClick={toggleSound}>
        <img
          src={soundIcon}
          alt="Sound Icon"
          className={`sound-icon ${isPlaying ? 'playing' : ''}`}
        />
      </div>
    </div>
  );
};

export default Home;
