import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // Import the CSS file for styling
import soundIcon from './assets/sound-icon.png'; // Add your sound icon image here
import songFile from './assets/song.mp3'; // Add your song file here

const Home = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio] = useState(new Audio(songFile)); // Persist the audio instance

  const toggleSound = () => {
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  // Clean up the audio when the component unmounts
  useEffect(() => {
    return () => {
      audio.pause();
      audio.currentTime = 0; // Reset audio to the beginning
    };
  }, [audio]);

  return (
    <div className="home-page">
      <h1 className="welcome-heading">Welcome to the 90's Vibes!</h1>
      <div className="button-container">
        <Link to="/downloads">
          <button>Downloads</button>
        </Link>
        <Link to="/characters">
          <button>Characters</button>
        </Link>
        <Link to="/games">
          <button>Fun & Games</button>
        </Link>
        <Link to="/crew">
          <button>Caste & Crew</button>
        </Link>
        <Link to="/gallery">
          <button>Gallery</button>
        </Link>
        <Link to="/review">
          <button>Reviews</button>
        </Link>
      </div>
      <div className="sound-toggle" onClick={toggleSound}>
        <img
          src={soundIcon}
          alt="Sound Icon"
          className={`sound-icon ${isPlaying ? 'playing' : ''}`}
        />
      </div>
    </div>
  );
};

export default Home;
